import React, {useMemo} from "react"
import "./index.scss"
import {Card, Col, Container, Row} from "react-bootstrap";
import {LineChart, Tooltip,XAxis,CartesianGrid,Line} from "recharts"
import data from "../data.json"

const keys = ["2019-nCoV", "Swine Flu", "SARS"]

const calculate = (days, category) => {
    const tmp = []

    for (let x = 0; x < days; x++) {
        keys.forEach(decease => {
            if (!tmp[x])
                tmp[x] = {'name': 'Day ' + (x+1)}

            let amount = data[category][decease][x];
            tmp[x][decease] = amount ? amount : 0
        })
    }

    return tmp
}

export default function Index() {

    console.log(data);

    const death100 = useMemo(() => calculate(100, 'deaths'), [])
    const death45 = useMemo(() => calculate(45, 'deaths'), [])
    const infected100 = useMemo(() => calculate(100, 'infected'), [])
    const infected45 = useMemo(() => calculate(45, 'infected'), [])


    return (
        <Container>
            <Row><Col xs={12}>
                <h1>2019 Novel Coronavirus (2019-nCoV)</h1>
                <p className={"text-center"}>Last updated: {data.lastUpdatedAt}</p>
            </Col></Row>
            <Row><Col sm={6}>
                <Chart title="Deaths over time" subtitle={"100 days"} data={death100} />
            </Col>
                <Col sm={6}>
                    <Chart title="Infections over time" subtitle={"100 days"} />
                </Col></Row>
            <Row><Col sm={6}>
                <Chart title="Deaths over time" subtitle={"45 days"} />
            </Col>
                <Col sm={6}>
                    <Chart title="Infections over time" subtitle={"45 days"} />
                </Col></Row>
        </Container>
    )
}



const Chart = ({title, subtitle, data}) => {

    const data2 = [
        {
            name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
        },
        {
            name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
        },
        {
            name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
        },
        {
            name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
        },
        {
            name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
        },
        {
            name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
        },
        {
            name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
        },
    ];

    return <Card>
        <Card.Header>{title}</Card.Header>
        <Card.Body>
            <LineChart
                width={400}
                height={400}
                data={data}
                margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
            >
                <XAxis dataKey="name" />
                <Tooltip />
                <CartesianGrid stroke="#f5f5f5" />
                {keys.map(d => (
                    <Line type="monotone" key={d} dataKey={d} stroke="#ff7300" yAxisId={0} />
                ))}
            </LineChart>
            <p>{subtitle}</p>
        </Card.Body>
    </Card>
}
